import { useGameStore } from '../store/gameStore'
import { winner } from '../utils/winner'
import { defineProps, reactive, toRefs, computed } from 'vue'
const props = defineProps(['position'])
const store = useGameStore()
const data = reactive({ pos: props.position, cell: null })
const getPlayer = computed(() => { return store.currentPlayer() })
const getCount = computed(() => { return store.count })
const getResult = computed(() => { return store.result })
const getHistory = computed(() => { return store.history })
const { pos, cell } = toRefs(data)
watch([getResult], () => {
    cell.value = null
    console.log(store.history)
})
const handleClick = () => {
    const a = store.getCount() % 2 == 0 ? 'X' : 'O'
    if (cell.value === null) {
        cell.value = a
        store.renewHistory(pos)
        store.addCount();
    }
    const res = winner(getHistory)
    if (res.result) {
        alert(`${res.winner}`)
        store.setResult();
        setTimeout(() => { store.$reset(); }, 10)
    }
}
